if (document.body.contains(document.getElementById("loadmore"))) {
  const e = document.getElementById("loadmore"),
    t = [...document.querySelectorAll(".hidden")];
  t.splice(0, 9).forEach((e) => e.classList.remove("hidden")),
    loadmore.addEventListener("click", function (a) {
      a.preventDefault(),
        t.splice(0, 6).forEach((e) => e.classList.remove("hidden")),
        0 == t.length && e.classList.add("hidden");
    });
}
